function enrollment_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('enrollment');
	if(!$arrPerm[1]){
		return false;
	}

	$data['stud_id'] = makeSafe($_REQUEST['stud_id']);
		if($data['stud_id'] == empty_lookup_value){ $data['stud_id'] = ''; }
	$data['course_id'] = makeSafe($_REQUEST['course_id']);
		if($data['course_id'] == empty_lookup_value){ $data['course_id'] = ''; }
	$data['score'] = makeSafe($_REQUEST['score']);
		if($data['score'] == empty_lookup_value){ $data['score'] = ''; }
	$data['notes'] = makeSafe($_REQUEST['notes']);
		if($data['notes'] == empty_lookup_value){ $data['notes'] = ''; }
	$data['certificate_notes'] = makeSafe($_REQUEST['certificate_notes']);
		if($data['certificate_notes'] == empty_lookup_value){ $data['certificate_notes'] = ''; }

	// hook: enrollment_before_insert
	if(function_exists('enrollment_before_insert')){
		$args=array();
		if(!enrollment_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `enrollment` set       `stud_id`=' . (($data['stud_id'] !== '' && $data['stud_id'] !== NULL) ? "'{$data['stud_id']}'" : 'NULL') . ', `course_id`=' . (($data['course_id'] !== '' && $data['course_id'] !== NULL) ? "'{$data['course_id']}'" : 'NULL') . ', `score`=' . (($data['score'] !== '' && $data['score'] !== NULL) ? "'{$data['score']}'" : 'NULL') . ', `notes`=' . (($data['notes'] !== '' && $data['notes'] !== NULL) ? "'{$data['notes']}'" : 'NULL') . ', `certificate_notes`=' . (($data['certificate_notes'] !== '' && $data['certificate_notes'] !== NULL) ? "'{$data['certificate_notes']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"enrollment_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: enrollment_after_insert
	if(function_exists('enrollment_after_insert')){
		$res = sql("select * from `enrollment` where `rec_id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!enrollment_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='enrollment', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

function enrollment_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('enrollment');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='enrollment' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='enrollment' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: enrollment_before_delete
	if(function_exists('enrollment_before_delete')){
		$args=array();
		if(!enrollment_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: attendance
	$res = sql("select `rec_id` from `enrollment` where `rec_id`='$selected_id'", $eo);
	$rec_id = db_fetch_row($res);
	$rires = sql("select count(1) from `attendance` where `student_course`='".addslashes($rec_id[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "attendance", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "attendance", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='enrollment_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='enrollment_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `enrollment` where `rec_id`='$selected_id'", $eo);

	// hook: enrollment_after_delete
	if(function_exists('enrollment_after_delete')){
		$args=array();
		enrollment_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='enrollment' and pkValue='$selected_id'", $eo);
}

function enrollment_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('enrollment');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='enrollment' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='enrollment' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['stud_id'] = makeSafe($_REQUEST['stud_id']);
		if($data['stud_id'] == empty_lookup_value){ $data['stud_id'] = ''; }
	$data['course_id'] = makeSafe($_REQUEST['course_id']);
		if($data['course_id'] == empty_lookup_value){ $data['course_id'] = ''; }
	$data['score'] = makeSafe($_REQUEST['score']);
		if($data['score'] == empty_lookup_value){ $data['score'] = ''; }
	$data['notes'] = makeSafe($_REQUEST['notes']);
		if($data['notes'] == empty_lookup_value){ $data['notes'] = ''; }
	$data['certificate_notes'] = makeSafe($_REQUEST['certificate_notes']);
		if($data['certificate_notes'] == empty_lookup_value){ $data['certificate_notes'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: enrollment_before_update
	if(function_exists('enrollment_before_update')){
		$args=array();
		if(!enrollment_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `enrollment` set       `stud_id`=' . (($data['stud_id'] !== '' && $data['stud_id'] !== NULL) ? "'{$data['stud_id']}'" : 'NULL') . ', `course_id`=' . (($data['course_id'] !== '' && $data['course_id'] !== NULL) ? "'{$data['course_id']}'" : 'NULL') . ', `score`=' . (($data['score'] !== '' && $data['score'] !== NULL) ? "'{$data['score']}'" : 'NULL') . ', `notes`=' . (($data['notes'] !== '' && $data['notes'] !== NULL) ? "'{$data['notes']}'" : 'NULL') . ', `certificate_notes`=' . (($data['certificate_notes'] !== '' && $data['certificate_notes'] !== NULL) ? "'{$data['certificate_notes']}'" : 'NULL') . " where `rec_id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="enrollment_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: enrollment_after_update
	if(function_exists('enrollment_after_update')){
		$res = sql("SELECT * FROM `enrollment` WHERE `rec_id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['rec_id'];
		$args = array();
		if(!enrollment_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='enrollment' and pkValue='".makeSafe($selected_id)."'", $eo);

}

