			$templateCode=str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return enrollment_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		}else{
			$templateCode=str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
			$templateCode=str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		}else{
			$templateCode=str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode=str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	}else{
		$templateCode=str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode=str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode=str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#stud_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#stud_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#course_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#course_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#score').replaceWith('<div class=\"form-control-static\" id=\"score\">' + (jQuery('#score').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode=str_replace('<%%COMBO(stud_id)%%>', $combo_stud_id->HTML, $templateCode);
	$templateCode=str_replace('<%%COMBOTEXT(stud_id)%%>', $combo_stud_id->MatchText, $templateCode);
	$templateCode=str_replace('<%%URLCOMBOTEXT(stud_id)%%>', urlencode($combo_stud_id->MatchText), $templateCode);
	$templateCode=str_replace('<%%COMBO(course_id)%%>', $combo_course_id->HTML, $templateCode);
	$templateCode=str_replace('<%%COMBOTEXT(course_id)%%>', $combo_course_id->MatchText, $templateCode);
	$templateCode=str_replace('<%%URLCOMBOTEXT(course_id)%%>', urlencode($combo_course_id->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'stud_id' => array('students', 'Student'), 'course_id' => array('courses', 'Course'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode=str_replace('<%%UPLOADFILE(rec_id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(stud_id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(course_id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(score)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(notes)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(certificate_notes)%%>', '', $templateCode);

	// process values
	if($selected_id){
		$templateCode=str_replace('<%%VALUE(rec_id)%%>', html_attr($row['rec_id']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(rec_id)%%>', urlencode($urow['rec_id']), $templateCode);
		$templateCode=str_replace('<%%VALUE(stud_id)%%>', html_attr($row['stud_id']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(stud_id)%%>', urlencode($urow['stud_id']), $templateCode);
		$templateCode=str_replace('<%%VALUE(course_id)%%>', html_attr($row['course_id']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(course_id)%%>', urlencode($urow['course_id']), $templateCode);
		$templateCode=str_replace('<%%VALUE(score)%%>', html_attr($row['score']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(score)%%>', urlencode($urow['score']), $templateCode);
		if($AllowUpdate || $AllowInsert){
			$templateCode = str_replace('<%%HTMLAREA(notes)%%>', '<textarea name="notes" id="notes" rows="5">' . html_attr($row['notes']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(notes)%%>', '<div id="notes" class="form-control-static">' . $row['notes'] . '</div>', $templateCode);
		}
		$templateCode=str_replace('<%%VALUE(notes)%%>', nl2br($row['notes']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(notes)%%>', urlencode($urow['notes']), $templateCode);
		if($AllowUpdate || $AllowInsert){
			$templateCode = str_replace('<%%HTMLAREA(certificate_notes)%%>', '<textarea name="certificate_notes" id="certificate_notes" rows="5">' . html_attr($row['certificate_notes']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(certificate_notes)%%>', '<div id="certificate_notes" class="form-control-static">' . $row['certificate_notes'] . '</div>', $templateCode);
		}
		$templateCode=str_replace('<%%VALUE(certificate_notes)%%>', nl2br($row['certificate_notes']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(certificate_notes)%%>', urlencode($urow['certificate_notes']), $templateCode);
	}else{
		$templateCode=str_replace('<%%VALUE(rec_id)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(rec_id)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(stud_id)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(stud_id)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(course_id)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(course_id)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(score)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(score)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%HTMLAREA(notes)%%>', '<textarea name="notes" id="notes" rows="5"></textarea>', $templateCode);
		$templateCode=str_replace('<%%HTMLAREA(certificate_notes)%%>', '<textarea name="certificate_notes" id="certificate_notes" rows="5"></textarea>', $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('enrollment');
	if($selected_id){
		$jdata = get_joined_record('enrollment', $selected_id);
		$rdata = $row;
	}
	$cache_data = array(
		'rdata' => array_map('nl2br', array_map('addslashes', $rdata)),
		'jdata' => array_map('nl2br', array_map('addslashes', $jdata)),
	);
	$templateCode .= loadView('enrollment-ajax-cache', $cache_data);

	// hook: enrollment_dv
	if(function_exists('enrollment_dv')){
		$args=array();
		enrollment_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>